AddLine.code:
// Appends a text line without modification.
AddLine()
{
  const tb = new LJCTextBuilder();

  // Example Method:
  tb.AddLine("This is an appended line.");

  tb.AddText(":");
  const result = tb.ToString();

  // result:
  // "This is an appended line."
  // ":"
}

