Line.code:
// Appends a potentially indented text line to the builder.
Line()
{
  const tb = new LJCTextBuilder();
  tb.AddIndent();

  // Example Method:
  // Starts the text with a newline if the builder already has text, param
  // allowNewLine = true and builder text does not end with a newline.
  // The text begins with the current indent string if param
  // addIndent = true.
  // Ends the text with a newline.
  tb.Line("First Line");
  tb.Line("This is some indented text.");

  const result = tb.ToString();

  // result:
  // "  First Line"
  // "  This is some indented text."
  // ""
}

Text.code:
// Appends the potentially indented text.
Text()
{
  const tb = new LJCTextBuilder();
  tb.AddIndent();

  // Example Method:
  // Starts the text with a newline if the builder already has text, param
  // allowNewLine = true and builder text does not end with a newline.
  // The text begins with the current indent string if param
  // addIndent = true.
  tb.Text("First Line");
  tb.Text("This is some indented text.");

  const result = tb.ToString();

  // result:
  // "First Line"
  // "  This is some indented text."
}

GetLine.code:
// Gets a modified text line.
GetLine()
{
  const tb = new LJCTextBuilder();

  tb.AddText("This is an appended line.");
  tb.AddIndent();

  // Example Method:
  // Starts the text with a newline if the builder already has text, param
  // allowNewLine = true and builder text does not end with a newline.
  // The text begins with the current indent string if param
  // addIndent = true.
  // Ends the text with a newline.
  let result = tb.GetLine("First Line");
  result += tb.GetLine("This is some indented text.");

  // result:
  // "  First Line"
  // "  This is some indented text."
  // ""
}

GetText.code:
// Gets the potentially indented and wrapped text.
GetText()
{
  const tb = new LJCTextBuilder();
  tb.AddIndent();

  // Example Method:
  // Starts the text with a newline if the builder already has text, param
  // allowNewLine = true and builder text does not end with a newline.
  // The text begins with the current indent string if param
  // addIndent = true.
  let result = tb.GetText("First Line");
  result += tb.GetText("This is some indented text.");

  // result:
  // "  First Line  This is some indented text."
}

