StartWithNewLine.code:
// Checks if the text can start with a newline.
StartWithNewLine()
{
  const tb = new LJCTextBuilder();
  tb.Text("First Line");
  const allowNewLine = true;

  // Example Method:
  let result = tb.StartWithNewLine(allowNewLine);

  // result:
  // true

  tb.Line();

  // Example Method:
  result = tb.StartWithNewLine(allowNewLine);

  // result:
  // false
}

