// result:
//   :

GetWrapped.code:
$tb = new LJCTextBuilder();
$tb->WrapEnabled = true;

// Example Method:
$tb->AddText("Now is the time for all good men to come to the aid of their");
$tb->AddText(" country.");
$tb->AddText(" Now is the time for all good men to come to the aid of their");
$tb->AddText(" country.");
$text = $tb->ToString();
$result = $tb->GetWrapped($text);

// result:
// Now is the time for all good men to come to the aid of
//  their country. Now is the
// time for all good men to come to the aid of their country.

Attribs.code:
// Root Method Begin
$textState = new LJCTextState();

$tb = new LJCTextBuilder($textState);

// Example Method:
$className = "className";
$id = "id";
$attribs = $tb->Attribs($className, $id);

$result = $tb->GetAttribs($attribs, $textState);

// result:
//  id="id" class="className"

